package com.refactoring.firstexample;public class Movie {    public static final int REGULAR = 0;    public static final int NEW_RELEASE = 1;    public static final int CHILDRENS = 2;    private String _title;    private int _priceCode;    Price _price;    public Movie(String _title, int _priceCode, Price price) {        this._title = _title;        _price = price;        setPriceCode(_priceCode);    }    double getCharge(int daysRented) {        return _price.getCharge(daysRented);    }    public String getTitle() {        return _title;    }    public int getPriceCode() {        return _price.getPriceCode();    }    public void setPriceCode(int _priceCode) {        switch (_priceCode) {            case REGULAR:                _price = new RegularPrice();                break;            case CHILDRENS:                _price = new ChildrensPrice();                break;            case NEW_RELEASE:                _price = new NewReleasePrice();                break;            default:                throw new IllegalArgumentException("Incorrect Price Code");        }    }    int getFrequentRenterPoints(int daysRented) {        return _price.getFrequentRenterPoints(daysRented);    }}